import React from 'react';
import { AutoLinkHeader, Example, Link as PatternflyThemeLink } from 'theme-patternfly-org/components';
import FileUploadIcon from '@patternfly/react-icons/dist/esm/icons/file-upload-icon';
const pageData = {
  "id": "File upload",
  "section": "components",
  "source": "react",
  "slug": "/components/file-upload/react",
  "sourceLink": "https://github.com/patternfly/patternfly-react/blob/main/packages/docs/packages/react-core/src/components/FileUpload/examples/FileUpload.md",
  "propComponents": [
    {
      "name": "FileUpload",
      "description": "",
      "props": [
        {
          "name": "allowEditingUploadedText",
          "type": "boolean",
          "description": "Flag to allow editing of a text file's contents after it is selected from disk"
        },
        {
          "name": "aria-label",
          "type": "string",
          "description": "Aria-label for the TextArea."
        },
        {
          "name": "browseButtonText",
          "type": "string",
          "description": "Text for the Browse button"
        },
        {
          "name": "children",
          "type": "React.ReactNode",
          "description": "Additional children to render after (or instead of) the file preview.",
          "defaultValue": "null"
        },
        {
          "name": "className",
          "type": "string",
          "description": "Additional classes added to the FileUpload container element."
        },
        {
          "name": "clearButtonText",
          "type": "string",
          "description": "Text for the Clear button"
        },
        {
          "name": "dropzoneProps",
          "type": "DropzoneProps",
          "description": "Optional extra props to customize react-dropzone.",
          "defaultValue": "{}"
        },
        {
          "name": "filename",
          "type": "string",
          "description": "Value to be shown in the read-only filename field.",
          "defaultValue": "''"
        },
        {
          "name": "filenameAriaLabel",
          "type": "string",
          "description": "Aria-label for the read-only filename field"
        },
        {
          "name": "filenamePlaceholder",
          "type": "string",
          "description": "Placeholder string to display in the empty filename field"
        },
        {
          "name": "hideDefaultPreview",
          "type": "boolean",
          "description": "Flag to hide the built-in preview of the file (where available).\nIf true, you can use children to render an alternate preview."
        },
        {
          "name": "id",
          "type": "string",
          "description": "Unique id for the TextArea, also used to generate ids for accessible labels.",
          "required": true
        },
        {
          "name": "isDisabled",
          "type": "boolean",
          "description": "Flag to show if the field is disabled."
        },
        {
          "name": "isLoading",
          "type": "boolean",
          "description": "Flag to show if a file is being loaded."
        },
        {
          "name": "isReadOnly",
          "type": "boolean",
          "description": "Flag to show if the field is read only."
        },
        {
          "name": "isRequired",
          "type": "boolean",
          "description": "Flag to show if the field is required."
        },
        {
          "name": "onChange",
          "type": "(\n  value: string | File,\n  filename: string,\n  event:\n    | React.MouseEvent<HTMLButtonElement, MouseEvent> // Clear button was clicked\n    | React.DragEvent<HTMLElement> // User dragged/dropped a file\n    | React.ChangeEvent<HTMLElement> // User typed in the TextArea\n) => void",
          "description": "A callback for when the file contents change. Please instead use onFileInputChange, onTextChange, onDataChange, onClearClick individually.",
          "defaultValue": "() => {}",
          "deprecated": true
        },
        {
          "name": "onClearClick",
          "type": "React.MouseEventHandler<HTMLButtonElement>",
          "description": "Clear button was clicked"
        },
        {
          "name": "onClick",
          "type": "(event: React.MouseEvent) => void",
          "description": "Callback for clicking on the FileUploadField text area. By default, prevents a click in the text area from opening file dialog.",
          "defaultValue": "event => event.preventDefault()"
        },
        {
          "name": "onDataChange",
          "type": "(data: string) => void",
          "description": "On data changed - if type='text' or type='dataURL' and file was loaded it will call this method"
        },
        {
          "name": "onFileInputChange",
          "type": "(event: React.ChangeEvent<HTMLInputElement> | React.DragEvent<HTMLElement>, file: File) => void",
          "description": "Change event emitted from the hidden \\<input type=\"file\" \\> field associated with the component",
          "defaultValue": "null"
        },
        {
          "name": "onReadFailed",
          "type": "(error: DOMException, fileHandle: File) => void",
          "description": "A callback for when the FileReader API fails",
          "defaultValue": "() => {}"
        },
        {
          "name": "onReadFinished",
          "type": "(fileHandle: File) => void",
          "description": "A callback for when a selected file finishes loading",
          "defaultValue": "() => {}"
        },
        {
          "name": "onReadStarted",
          "type": "(fileHandle: File) => void",
          "description": "A callback for when a selected file starts loading",
          "defaultValue": "() => {}"
        },
        {
          "name": "onTextChange",
          "type": "(text: string) => void",
          "description": "Text area text changed"
        },
        {
          "name": "spinnerAriaValueText",
          "type": "string",
          "description": "Aria-valuetext for the loading spinner"
        },
        {
          "name": "type",
          "type": "'text' | 'dataURL'",
          "description": "What type of file. Determines what is is passed to `onChange` and expected by `value`\n(a string for 'text' and 'dataURL', or a File object otherwise."
        },
        {
          "name": "validated",
          "type": "'success' | 'error' | 'default'",
          "description": "Value to indicate if the field is modified to show that validation state.\nIf set to success, field will be modified to indicate valid state.\nIf set to error,  field will be modified to indicate error state."
        },
        {
          "name": "value",
          "type": "string | File",
          "description": "Value of the file's contents\n(string if text file, File object otherwise)",
          "defaultValue": "type === fileReaderType.text || type === fileReaderType.dataURL ? '' : null"
        }
      ]
    },
    {
      "name": "FileUploadField",
      "description": "",
      "props": [
        {
          "name": "allowEditingUploadedText",
          "type": "boolean",
          "description": "Flag to allow editing of a text file's contents after it is selected from disk",
          "defaultValue": "false"
        },
        {
          "name": "aria-label",
          "type": "string",
          "description": "Aria-label for the TextArea.",
          "defaultValue": "'File upload'"
        },
        {
          "name": "browseButtonText",
          "type": "string",
          "description": "Text for the Browse button",
          "defaultValue": "'Browse...'"
        },
        {
          "name": "children",
          "type": "React.ReactNode",
          "description": "Additional children to render after (or instead of) the file preview.",
          "defaultValue": "null"
        },
        {
          "name": "className",
          "type": "string",
          "description": "Additional classes added to the FileUploadField container element.",
          "defaultValue": "''"
        },
        {
          "name": "clearButtonText",
          "type": "string",
          "description": "Text for the Clear button",
          "defaultValue": "'Clear'"
        },
        {
          "name": "containerRef",
          "type": "React.Ref<HTMLDivElement>",
          "description": "A reference object to attach to the FileUploadField container element.",
          "defaultValue": "null"
        },
        {
          "name": "filename",
          "type": "string",
          "description": "Value to be shown in the read-only filename field.",
          "defaultValue": "''"
        },
        {
          "name": "filenameAriaLabel",
          "type": "string",
          "description": "Aria-label for the read-only filename field",
          "defaultValue": "filename ? 'Read only filename' : filenamePlaceholder"
        },
        {
          "name": "filenamePlaceholder",
          "type": "string",
          "description": "Placeholder string to display in the empty filename field",
          "defaultValue": "'Drag a file here or browse to upload'"
        },
        {
          "name": "hideDefaultPreview",
          "type": "boolean",
          "description": "Flag to hide the built-in preview of the file (where available).\nIf true, you can use children to render an alternate preview.",
          "defaultValue": "false"
        },
        {
          "name": "id",
          "type": "string",
          "description": "Unique id for the TextArea, also used to generate ids for accessible labels",
          "required": true
        },
        {
          "name": "isClearButtonDisabled",
          "type": "boolean",
          "description": "Flag to disable the Clear button",
          "defaultValue": "!filename && !value"
        },
        {
          "name": "isDisabled",
          "type": "boolean",
          "description": "Flag to show if the field is disabled.",
          "defaultValue": "false"
        },
        {
          "name": "isDragActive",
          "type": "boolean",
          "description": "Flag to show if a file is being dragged over the field",
          "defaultValue": "false"
        },
        {
          "name": "isLoading",
          "type": "boolean",
          "description": "Flag to show if a file is being loaded.",
          "defaultValue": "false"
        },
        {
          "name": "isReadOnly",
          "type": "boolean",
          "description": "Flag to show if the field is read only.",
          "defaultValue": "false"
        },
        {
          "name": "isRequired",
          "type": "boolean",
          "description": "Flag to show if the field is required.",
          "defaultValue": "false"
        },
        {
          "name": "onBrowseButtonClick",
          "type": "(event: React.MouseEvent<HTMLButtonElement, MouseEvent>) => void",
          "description": "A callback for when the Browse button is clicked.",
          "defaultValue": "() => {}"
        },
        {
          "name": "onChange",
          "type": "(\n  value: string,\n  filename: string,\n  event:\n    | React.ChangeEvent<HTMLTextAreaElement> // User typed in the TextArea\n    | React.MouseEvent<HTMLButtonElement, MouseEvent> // User clicked Clear button\n) => void",
          "description": "A callback for when the TextArea value changes.",
          "defaultValue": "() => {}"
        },
        {
          "name": "onClearButtonClick",
          "type": "(event: React.MouseEvent<HTMLButtonElement, MouseEvent>) => void",
          "description": "A callback for when the Clear button is clicked.",
          "defaultValue": "() => {}"
        },
        {
          "name": "onTextAreaClick",
          "type": "(event: React.MouseEvent<HTMLTextAreaElement, MouseEvent>) => void",
          "description": "A callback from when the text area is clicked. Can also be set via the onClick property of FileUpload."
        },
        {
          "name": "onTextChange",
          "type": "(text: string) => void",
          "description": "Text area text changed"
        },
        {
          "name": "spinnerAriaValueText",
          "type": "string",
          "description": "Aria-valuetext for the loading spinner"
        },
        {
          "name": "type",
          "type": "'text' | 'dataURL'",
          "description": "What type of file. Determines what is is expected by `value`\n(a string for 'text' and 'dataURL', or a File object otherwise)."
        },
        {
          "name": "validated",
          "type": "'success' | 'error' | 'default'",
          "description": "Value to indicate if the field is modified to show that validation state.\nIf set to success, field will be modified to indicate valid state.\nIf set to error,  field will be modified to indicate error state.",
          "defaultValue": "'default'"
        },
        {
          "name": "value",
          "type": "string | File",
          "description": "Value of the file's contents\n(string if text file, File object otherwise)",
          "defaultValue": "''"
        }
      ]
    }
  ],
  "cssPrefix": [
    "pf-c-file-upload"
  ],
  "examples": [
    "Simple text file",
    "Text file with edits allowed",
    "Text file with restrictions",
    "Simple file of any format",
    "Custom file preview",
    "Custom file upload"
  ]
};
pageData.liveContext = {
  FileUploadIcon
};
pageData.examples = {
  'Simple text file': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { FileUpload } from '@patternfly/react-core';\n\nclass SimpleTextFileUpload extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = { value: '', filename: '', isLoading: false };\n    this.handleFileInputChange = (event, file) => this.setState({ filename: file.name });\n    this.handleTextOrDataChange = value => this.setState({ value });\n    this.handleClear = event => this.setState({ filename: '', value: '' });\n    this.handleFileReadStarted = fileHandle => this.setState({ isLoading: true });\n    this.handleFileReadFinished = fileHandle => this.setState({ isLoading: false });\n  }\n\n  render() {\n    const { value, filename, isLoading } = this.state;\n    return (\n      <FileUpload\n        id=\"simple-text-file\"\n        type=\"text\"\n        value={value}\n        filename={filename}\n        filenamePlaceholder=\"Drag and drop a file or upload one\"\n        onFileInputChange={this.handleFileInputChange}\n        onDataChange={this.handleTextOrDataChange}\n        onTextChange={this.handleTextOrDataChange}\n        onReadStarted={this.handleFileReadStarted}\n        onReadFinished={this.handleFileReadFinished}\n        onClearClick={this.handleClear}\n        isLoading={isLoading}\n        browseButtonText=\"Upload\"\n      />\n    );\n  }\n}","title":"Simple text file","lang":"js"}}>

    </Example>,
  'Text file with edits allowed': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { FileUpload } from '@patternfly/react-core';\n\nclass TextFileWithEditsAllowed extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = { value: '', filename: '', isLoading: false };\n    this.handleFileInputChange = (event, file) => this.setState({ filename: file.name });\n    this.handleTextOrDataChange = value => this.setState({ value });\n    this.handleClear = event => this.setState({ filename: '', value: '' });\n    this.handleFileReadStarted = fileHandle => this.setState({ isLoading: true });\n    this.handleFileReadFinished = fileHandle => this.setState({ isLoading: false });\n  }\n\n  render() {\n    const { value, filename, isLoading } = this.state;\n    return (\n      <FileUpload\n        id=\"text-file-with-edits-allowed\"\n        type=\"text\"\n        value={value}\n        filename={filename}\n        filenamePlaceholder=\"Drag and drop a file or upload one\"\n        onFileInputChange={this.handleFileInputChange}\n        onDataChange={this.handleTextOrDataChange}\n        onClearClick={this.handleClear}\n        onTextChange={this.handleTextOrDataChange}\n        onReadStarted={this.handleFileReadStarted}\n        onReadFinished={this.handleFileReadFinished}\n        isLoading={isLoading}\n        allowEditingUploadedText\n        browseButtonText=\"Upload\"\n      />\n    );\n  }\n}","title":"Text file with edits allowed","lang":"js"}}>

    </Example>,
  'Text file with restrictions': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { FileUpload, Form, FormGroup } from '@patternfly/react-core';\n\nclass TextFileUploadWithRestrictions extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = { value: '', filename: '', isLoading: false, isRejected: false };\n    this.handleFileInputChange = (event, file) => this.setState({ filename: file.name });\n    this.handleTextOrDataChange = value => this.setState({ value });\n    this.handleClear = event => this.setState({ filename: '', value: '', isRejected: false });\n    this.handleFileRejected = (rejectedFiles, event) => this.setState({ isRejected: true });\n    this.handleFileReadStarted = fileHandle => this.setState({ isLoading: true });\n    this.handleFileReadFinished = fileHandle => this.setState({ isLoading: false });\n  }\n\n  render() {\n    const { value, filename, isLoading, isRejected } = this.state;\n    return (\n      <Form>\n        <FormGroup\n          fieldId=\"text-file-with-restrictions\"\n          helperText=\"Upload a CSV file\"\n          helperTextInvalid=\"Must be a CSV file no larger than 1 KB\"\n          validated={isRejected ? 'error' : 'default'}\n        >\n          <FileUpload\n            id=\"text-file-with-restrictions\"\n            type=\"text\"\n            value={value}\n            filename={filename}\n            filenamePlaceholder=\"Drag and drop a file or upload one\"\n            onFileInputChange={this.handleFileInputChange}\n            onDataChange={this.handleTextOrDataChange}\n            onTextChange={this.handleTextOrDataChange}\n            onClearClick={this.handleClear}\n            onReadStarted={this.handleFileReadStarted}\n            onReadFinished={this.handleFileReadFinished}\n            isLoading={isLoading}\n            dropzoneProps={{\n              accept: '.csv',\n              maxSize: 1024,\n              onDropRejected: this.handleFileRejected\n            }}\n            validated={isRejected ? 'error' : 'default'}\n            browseButtonText=\"Upload\"\n          />\n        </FormGroup>\n      </Form>\n    );\n  }\n}","title":"Text file with restrictions","lang":"js"}}>

    </Example>,
  'Simple file of any format': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { FileUpload } from '@patternfly/react-core';\n\nclass SimpleFileUpload extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = { value: null, filename: '' };\n    this.handleFileInputChange = (event, file) => {\n      this.setState({ filename: file.name });\n    };\n    this.handleClear = event => this.setState({ filename: '', value: '' });\n  }\n\n  render() {\n    const { value, filename } = this.state;\n    return (\n      <FileUpload\n        id=\"simple-file\"\n        value={value}\n        filename={filename}\n        filenamePlaceholder=\"Drag and drop a file or upload one\"\n        browseButtonText=\"Upload\"\n        onFileInputChange={this.handleFileInputChange}\n        onClearClick={this.handleClear}\n      />\n    );\n  }\n}","title":"Simple file of any format","lang":"js"}}>

    </Example>,
  'Custom file preview': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';\nimport { FileUpload } from '@patternfly/react-core';\nimport FileUploadIcon from '@patternfly/react-icons/dist/esm/icons/file-upload-icon';\n\nclass CustomPreviewFileUpload extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = { value: null, filename: '' };\n    this.handleFileInputChange = (event, file) => {\n      this.setState({ value: file, filename: file.name });\n    };\n    this.handleClear = event => this.setState({ filename: '', value: '' });\n  }\n\n  render() {\n    const { value, filename, isLoading } = this.state;\n    return (\n      <FileUpload\n        id=\"customized-preview-file\"\n        value={value}\n        filename={filename}\n        filenamePlaceholder=\"Drag and drop a file or upload one\"\n        onFileInputChange={this.handleFileInputChange}\n        onClearClick={this.handleClear}\n        hideDefaultPreview\n        browseButtonText=\"Upload\"\n      >\n        {value && (\n          <div className=\"pf-u-m-md\">\n            <FileUploadIcon size=\"lg\" /> Custom preview here for your {value.size}-byte file named {value.name}\n          </div>\n        )}\n      </FileUpload>\n    );\n  }\n}","title":"Custom file preview","lang":"js"}}>

