import React from 'react';
import { AutoLinkHeader, Example, Link as PatternflyThemeLink } from 'theme-patternfly-org/components';
import DashboardWrapper from '../../../../node_modules/@patternfly/react-core/src/demos/./examples/DashboardWrapper';
import srcImportscrollspyh2elements from './react-demos/scrollspy-h2-elements.png';
import srcImportscrollspywithsubsections from './react-demos/scrollspy-with-subsections.png';
const pageData = {
  "id": "Jump links",
  "section": "components",
  "source": "react-demos",
  "slug": "/components/jump-links/react-demos",
  "sourceLink": "https://github.com/patternfly/patternfly-react/blob/main/packages/docs/packages/react-core/src/demos/JumpLinks.md",
  "fullscreenExamples": [
    "Scrollspy with subsections"
  ]
};
pageData.liveContext = {
  DashboardWrapper
};
pageData.examples = {
  'Scrollspy with subsections': props => 
    <Example {...pageData} {...props} thumbnail={srcImportscrollspywithsubsections} {...{"code":"import React from 'react';
import {
  PageSection,
  JumpLinks,
  JumpLinksItem,
  JumpLinksList,
  PageGroup,
  Sidebar,
  SidebarContent,
  SidebarPanel,
  Switch,
  Title,
  TextContent,
  getResizeObserver
} from '@patternfly/react-core';
import DashboardWrapper from './examples/DashboardWrapper';

ScrollspyH2 = () => {
  const headings = [1, 2, 3, 4, 5];

  const [isVertical, setIsVertical] = React.useState(true);
  const [offsetHeight, setOffsetHeight] = React.useState(10);

  // Update offset based on the masthead and jump links nav heights.
  React.useEffect(() => {
    const masthead = document.getElementsByClassName('pf-c-masthead')[0];
    const offsetForPadding = 10;

    getResizeObserver(masthead, () => {
      if (isVertical) {
        setOffsetHeight(masthead.offsetHeight + offsetForPadding);
      } else {
        // Append jump links nav height to the masthead height when value exists.
        const jumpLinksHeaderHeight = document.getElementsByClassName('pf-m-sticky')[0].offsetHeight;
        jumpLinksHeaderHeight && setOffsetHeight(masthead.offsetHeight + jumpLinksHeaderHeight + offsetForPadding);
      }
    });
  }, [isVertical]);

  return (
    <DashboardWrapper breadcrumb={null} mainContainerId="scrollable-element">
      <PageSection variant="light">
        <Title headingLevel="h1" size="2xl">
          Main title
        </Title>
        <Switch
          id="is-vertical"
          label="Vertical"
          labelOff="Horizontal"
          isChecked={isVertical}
          onChange={check => setIsVertical(check)}
        />
      </PageSection>
      <PageSection padding={{ default: 'noPadding' }}>
        <Sidebar hasGutter orientation={!isVertical && 'stack'}>
          <SidebarPanel variant="sticky">
            <PageSection>
              <JumpLinks
                isVertical={isVertical}
                isCentered={!isVertical}
                label="Jump to section"
                scrollableSelector="#scrollable-element"
                offset={offsetHeight}
                expandable={{ default: isVertical ? 'expandable' : 'nonExpandable', md: 'nonExpandable' }}
                isExpanded
              >
                {headings.map(i => (
                  <JumpLinksItem key={i} href={`#heading-${i}`}>
                    {`Heading ${i}`}
                    <JumpLinksList></JumpLinksList>
                  </JumpLinksItem>
                ))}
              </JumpLinks>
            </PageSection>
          </SidebarPanel>
          <SidebarContent hasNoBackground>
            <PageSection>
              <TextContent>
                {headings.map(i => (
                  <div key={i} style={{ maxWidth: '800px', marginBottom: '32px' }}>
                    <h2 id={`heading-${i}`} tabIndex={-1}>
                      {`Heading ${i}`}
                    </h2>
                    <p>
                      Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut
                      labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris
                      nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit
                      esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt
                      in culpa qui officia deserunt mollit anim id est laborum.
                    </p>
                    <br />
                    <p>
                      At vero eos et accusamus et iusto odio dignissimos ducimus qui blanditiis praesentium voluptatum
                      deleniti atque corrupti quos dolores et quas molestias excepturi sint occaecati cupiditate non
                      provident, similique sunt in culpa qui officia deserunt mollitia animi, id est laborum et dolorum
                      fuga. Et harum quidem rerum facilis est et expedita distinctio. Nam libero tempore, cum soluta
                      nobis est eligendi optio cumque nihil impedit quo minus id quod maxime placeat facere possimus,
                      omnis voluptas assumenda est, omnis dolor repellendus. Temporibus autem quibusdam et aut officiis
                      debitis aut rerum necessitatibus saepe eveniet ut et voluptates repudiandae sint et molestiae non
                      recusandae. Itaque earum rerum hic tenetur a sapiente delectus, ut aut reiciendis voluptatibus
                      maiores alias consequatur aut perferendis doloribus asperiores repellat.
                    </p>
                  </div>
                ))}
              </TextContent>
            </PageSection>
          </SidebarContent>
        </Sidebar>
      </PageSection>
    </DashboardWrapper>
  );
};","title":"Scrollspy with subsections","lang":"js","isFullscreen":true}}>

      <p {...{"className":"ws-p"}}>
        {`This demo expands on the previous to show the JumpLinks in a vertical layout with subsections. It scrolls the `}

        <PatternflyThemeLink {...{"to":"/components/page"}}>
          {`Page`}
        </PatternflyThemeLink>
        {`'s `}

        <code {...{"className":"ws-code"}}>
          {`mainContainerId`}
        </code>
        {` with an `}

        <code {...{"className":"ws-code"}}>
          {`offset`}
        </code>
        {` calculated based on the height of the masthead and the nav list when it appears above the content. The headings are given a tab index to allow the jump links to focus on them.`}
      </p>
    </Example>
};

const Component = () => (
  <React.Fragment>
    <AutoLinkHeader {...{"id":"demos","size":"h2","className":"ws-title ws-h2"}}>
      {`Demos`}
    </AutoLinkHeader>
    <p {...{"className":"ws-p"}}>
      {`JumpLinks has a scrollspy built-in to make your implementation easier. When implementing JumpLinks be sure to:`}
    </p>
    <ol {...{"className":"ws-ol"}}>
      <li {...{"className":"ws-li"}}>
        {`Find the correct `}
        <code {...{"className":"ws-code"}}>
          {`scrollableSelector`}
        </code>
        {` for your page via `}
        <PatternflyThemeLink {...{"to":"https://developer.mozilla.org/en-US/docs/Tools/Page_Inspector/How_to/Debug_Scrollable_Overflow"}}>
          {`Firefox's debugging scrollable overflow`}
        </PatternflyThemeLink>
        {` or by adding `}
        <code {...{"className":"ws-code"}}>
          {`hasOverflowScroll`}
        </code>
        {` to a `}
        <PatternflyThemeLink {...{"to":"/components/page#pagesection"}}>
          {`PageSection`}
        </PatternflyThemeLink>
        {` or `}
        <PatternflyThemeLink {...{"to":"/components/page#pagegroup"}}>
          {`PageGroup`}
        </PatternflyThemeLink>
        {`.`}
      </li>
      <li {...{"className":"ws-li"}}>
        {`Provide `}
        <code {...{"className":"ws-code"}}>
          {`href`}
        </code>
        {`s to your JumpLinksItems which match the `}
        <code {...{"className":"ws-code"}}>
          {`id`}
        </code>
        {` of elements you want to spy on. If you wish to scroll to a different item than you're linking to use the `}
        <code {...{"className":"ws-code"}}>
          {`node`}
        </code>
        {` prop.`}
      </li>
    </ol>
    {React.createElement(pageData.examples["Scrollspy with subsections"])}
  </React.Fragment>
);
Component.displayName = 'ComponentsJumpLinksReactDemosDocs';
Component.pageData = pageData;

export default Component;
