import React from 'react';
import { AutoLinkHeader, Example, Link as PatternflyThemeLink } from 'theme-patternfly-org/components';

const pageData = {
  "id": "Text input",
  "section": "components",
  "source": "react",
  "slug": "/components/text-input/react",
  "sourceLink": "https://github.com/patternfly/patternfly-react/blob/main/packages/docs/packages/react-core/src/components/TextInput/examples/TextInput.md",
  "propComponents": [
    {
      "name": "TextInput",
      "description": "",
      "props": [
        {
          "name": "aria-label",
          "type": "string",
          "description": "Aria-label. The input requires an associated id or aria-label."
        },
        {
          "name": "className",
          "type": "string",
          "description": "Additional classes added to the TextInput."
        },
        {
          "name": "customIconDimensions",
          "type": "string",
          "description": "Dimensions for the custom icon set as the input's background-size"
        },
        {
          "name": "customIconUrl",
          "type": "string",
          "description": "Custom icon url to set as the input's background-image"
        },
        {
          "name": "iconVariant",
          "type": "'calendar' | 'clock' | 'search'",
          "description": "icon variant"
        },
        {
          "name": "innerRef",
          "type": "React.RefObject<any>",
          "description": "A reference object to attach to the input box."
        },
        {
          "name": "isDisabled",
          "type": "boolean",
          "description": "Flag to show if the input is disabled."
        },
        {
          "name": "isIconSprite",
          "type": "boolean",
          "description": "Use the external file instead of a data URI"
        },
        {
          "name": "isLeftTruncated",
          "type": "boolean",
          "description": "Trim text on left"
        },
        {
          "name": "isReadOnly",
          "type": "boolean",
          "description": "Flag to show if the input is read only."
        },
        {
          "name": "isRequired",
          "type": "boolean",
          "description": "Flag to show if the input is required."
        },
        {
          "name": "onBlur",
          "type": "(event?: any) => void",
          "description": "Callback function when input is blurred (focus leaves)"
        },
        {
          "name": "onChange",
          "type": "(value: string, event: React.FormEvent<HTMLInputElement>) => void",
          "description": "A callback for when the input value changes."
        },
        {
          "name": "onFocus",
          "type": "(event?: any) => void",
          "description": "Callback function when input is focused"
        },
        {
          "name": "type",
          "type": "| 'text'
| 'date'
| 'datetime-local'
| 'email'
| 'month'
| 'number'
| 'password'
| 'search'
| 'tel'
| 'time'
| 'url'",
          "description": "Type that the input accepts."
        },
        {
          "name": "validated",
          "type": "'success' | 'warning' | 'error' | 'default'",
          "description": "Value to indicate if the input is modified to show that validation state.
If set to success, input will be modified to indicate valid state.
If set to error,  input will be modified to indicate error state."
        },
        {
          "name": "value",
          "type": "string | number",
          "description": "Value of the input."
        }
      ]
    }
  ],
  "cssPrefix": [
    "pf-c-form-control"
  ],
  "examples": [
    "Basic",
    "Disabled",
    "Truncated on Left",
    "Read only",
    "Invalid",
    "Select text using ref",
    "Icon variants",
    "Icon sprite variants"
  ]
};
pageData.examples = {
  'Basic': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

class SimpleTextInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: ''
    };
    this.handleTextInputChange = value => {
      this.setState({ value });
    };
  }

  render() {
    const { value } = this.state;

    return (
      <TextInput value={value} type="text" onChange={this.handleTextInputChange} aria-label="text input example" />
    );
  }
}","title":"Basic","lang":"js"}}>

    </Example>,
  'Disabled': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

const Disabled = () => (
  <TextInput
    value="disabled text input example"
    type="text"
    onChange={this.handleTextInputChange}
    aria-label="disabled text input example"
    isDisabled
  />
)","title":"Disabled","lang":"js"}}>

    </Example>,
  'Truncated on Left': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

class LeftTruncatedTextInput extends React.Component {

constructor(props) {
    super(props);
    this.state = {
      value: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.'
    };
    this.handleTextInputChange = value => {
      this.setState({ value });
    };
  }

  render() {
    const { value } = this.state;
    return (
      <TextInput isLeftTruncated value={value} type="text" onChange={this.handleTextInputChange} aria-label="left-truncated text input example"  />
    );
  }
}","title":"Truncated on Left","lang":"js"}}>

    </Example>,
  'Read only': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

const ReadOnly = () => <TextInput value="read only text input example" type="text" isReadOnly aria-label="readonly input example" />;","title":"Read only","lang":"js"}}>

    </Example>,
  'Invalid': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput, ValidatedOptions } from '@patternfly/react-core';

class InvalidTextInput extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: ''
    };
    this.handleInvalidTextInputChange = value => {
      this.setState({ value });
    };
  }

  render() {
    const { value } = this.state;

    return (
      <TextInput
        value={value}
        onChange={this.handleInvalidTextInputChange}
        isRequired
        validated={ValidatedOptions.error}
        type="text"
        aria-label="invalid text input example"
      />
    );
  }
}","title":"Invalid","lang":"js"}}>

    </Example>,
  'Select text using ref': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

TextInputSelectAll = () => {
  const [value, setValue] = React.useState('select all on click');
  const ref = React.useRef(null);
  return (
    <TextInput
      ref={ref}
      value={value}
      onFocus={() => ref && ref.current && ref.current.select()}
      onChange={value => setValue(value)}
      aria-label="select-all"
    />
  );
};","title":"Select text using ref","lang":"js"}}>

    </Example>,
  'Icon variants': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

SimpleTextInput = () => {
  const [calendar, setCalendar] = React.useState('');
  const [clock, setClock] = React.useState('');
  const [custom, setCustom] = React.useState('');

  return (
    <>
      <TextInput
        value={calendar}
        type="text"
        iconVariant="calendar"
        onChange={value => setCalendar(value)}
        aria-label="text input example"
      />
      <br />
      <br />
      <TextInput
        value={clock}
        type="text"
        iconVariant="clock"
        onChange={value => setClock(value)}
        aria-label="text input example"
      />
      <br />
      <br />
      <TextInput
        value={custom}
        type="text"
        customIconDimensions="24px 24px"
        customIconUrl='data:image/svg+xml;charset=utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512"%3E%3Cpath fill="%23a18fff" d="M158.87.15c-16.16-1.52-31.2 8.42-35.33 24.12l-14.81 56.27c187.62 5.49 314.54 130.61 322.48 317l56.94-15.78c15.72-4.36 25.49-19.68 23.62-35.9C490.89 165.08 340.78 17.32 158.87.15zm-58.47 112L.55 491.64a16.21 16.21 0 0 0 20 19.75l379-105.1c-4.27-174.89-123.08-292.14-299.15-294.1zM128 416a32 32 0 1 1 32-32 32 32 0 0 1-32 32zm48-152a32 32 0 1 1 32-32 32 32 0 0 1-32 32zm104 104a32 32 0 1 1 32-32 32 32 0 0 1-32 32z"/%3E%3C/svg%3E'
        onChange={value => setCustom(value)}
        aria-label="text input example"
      />
    </>
  );
};","title":"Icon variants","lang":"js"}}>

    </Example>,
  'Icon sprite variants': props => 
    <Example {...pageData} {...props} {...{"code":"import React from 'react';
import { TextInput } from '@patternfly/react-core';

IconSpriteTextInputs = () => {
  const [success, setSuccess] = React.useState('');
  const [warning, setWarning] = React.useState('');
  const [error, setError] = React.useState('');
  const [calendar, setCalendar] = React.useState('');
  const [clock, setClock] = React.useState('');

  return (
    <>
      <TextInput
        validated={ValidatedOptions.success}
        isIconSprite
        type="text"
        onChange={value => setSuccess(value)}
        aria-label="success icon sprite text input example"
      />
      <br />
      <br />
      <TextInput
        validated={ValidatedOptions.warning}
        isIconSprite
        type="text"
        onChange={value => setWarning(value)}
        aria-label="warning icon sprite text input example"
      />
      <br />
      <br />
      <TextInput
        validated={ValidatedOptions.error}
        isIconSprite
        type="text"
        onChange={value => setError(value)}
        aria-label="error icon sprite text input example"
      />
      <br />
      <br />
      <TextInput
        value={calendar}
        isIconSprite
        type="text"
        iconVariant="calendar"
        onChange={value => setCalendar(value)}
        aria-label="calendar icon sprite text input example"
      />
      <br />
      <br />
      <TextInput
        value={clock}
        isIconSprite
        type="text"
        iconVariant="clock"
        onChange={value => setClock(value)}
        aria-label="clock icon sprite text input example"
      />
    </>
  );
};","title":"Icon sprite variants","lang":"js","isBeta":true}}>

      <p {...{"className":"ws-p"}}>

        <strong>
          {`Note:`}
        </strong>
        {` The icons for the success, invalid, calendar, etc. variations in form control elements are applied as background images to the form element. By default, the image URLs for these icons are data URIs. However, there may be cases where data URIs are not ideal, such as in an application with a content security policy that disallows data URIs for security reasons. The `}

        <code {...{"className":"ws-code"}}>
          {`isIconSprite`}
        </code>
        {` variation changes the icon source to an external SVG file that serves as a sprite for all of the supported icons.`}
      </p>
    </Example>
};

const Component = () => (
  <React.Fragment>
    <AutoLinkHeader {...{"id":"examples","size":"h2","className":"ws-title ws-h2"}}>
      {`Examples`}
    </AutoLinkHeader>
